  const profilePhotoLink = "https://iili.io/JLBI7ku.md.jpg";

  describe('create', () => {
    const dtoExample = {UserId: userId,  ProfilePicture: profilePhoto }
    it('should create a profile picture', async () => {
      const dto: CreateProfilePictureDto = dtoExample;
      expect(await controller.create(dto)).toBe(undefined);
      expect(service.create).toHaveBeenCalledWith(dto);
    });

    it('should throw a NotFoundException if an error occurs', async () => {
      const dto: CreateProfilePictureDto = dtoExample;
      jest.spyOn(service, 'create').mockImplementation(() => {
        throw new NotFoundException('An unexpected error occurred');
      });
      await expect(controller.create(dto)).rejects.toThrow(NotFoundException);
    });
  });

  describe('getProfilePicture', () => {
    it('should get a profile picture', async () => {
      expect(await controller.getProfilePicture(userId)).toEqual({ profilePhotoLink });
      expect(service.getProfilePictureByUserId).toHaveBeenCalledWith(userId);
    });

    it('should throw a NotFoundException if an error occurs', async () => {
      jest.spyOn(service, 'getProfilePictureByUserId').mockImplementation(() => {
        throw new NotFoundException('An unexpected error occurred');
      });

      await expect(controller.getProfilePicture(userId)).rejects.toThrow(NotFoundException);
    });
  });

